mapper.select:
SELECT tad.`value` AS journalLevel FROM teacher_paper tp LEFT JOIN teacher_achievements_dict tad ON tad.type = 'journallevel'
and tp.id = #{id,jdbcType=VARCHAR}
and tad.`key` = #{journalLevel,jdbcType=VARCHAR}

select 

from teacher_paper


   and   id = #{id,jdbcType=VARCHAR}


    and  operator = #{operator,jdbcType=VARCHAR}

    SELECT

	FROM
	    teacher_user tu
	LEFT JOIN teacher_paper tp ON tu.teacher_id = tp.operator


         and tu.teacher_id = #{teacherId,jdbcType=VARCHAR}


          and tu.teacher_no LIKE '%${teacherNo}%'


         and tu.college = #{college,jdbcType=VARCHAR}


         and tu.major = #{major,jdbcType=VARCHAR}


         and tu.user_name LIKE '%${userName}%'

      AND tp.title IS NOT NULL
