mapper.select:
SELECT tad.`value` AS compilationForm FROM teacher_writings tw LEFT JOIN teacher_achievements_dict tad ON tad.type = 'compilationform'
and tw.id = #{id,jdbcType=VARCHAR}
and tad.`key` = #{compilationForm,jdbcType=VARCHAR}


select 

from teacher_writings


     and id = #{id,jdbcType=VARCHAR}


     and operator = #{operator,jdbcType=VARCHAR}

    SELECT

	FROM
	    teacher_user tu
	LEFT JOIN teacher_writings te on tu.teacher_id = te.operator

      te.id IS NOT NULL

         and tu.teacher_id = #{teacherId,jdbcType=VARCHAR}


          and tu.teacher_no LIKE '%${teacherNo}%'


         and tu.college = #{college,jdbcType=VARCHAR}


         and tu.major = #{major,jdbcType=VARCHAR}


         and tu.user_name LIKE '%${userName}%'
